import React from 'react'
import firebase from '../utils/firebase'
import '../App2.css'



function Data({data}) {
    var i = 1;

    if (i % 2 === 0){
        document.getElementById("colorpattern").style.backgroundColor = "rgb(189, 189, 189)";
    }

    i = i + 1;

    return (
        <div className="stu" id="colorpattern">
            <div>{data.name + " อ." + data.class + " เลขที่ " + data.number}</div>
        </div>
    )
}

export default Data;
