import React from 'react'
import firebase from '../utils/firebase'
import '../App2.css'

const i = 1

function Data({data}) {

    if (i % 2 === 0){
        document.getElementById("colorpattern").style.backgroundColor = "rgb(184, 209, 255)";
    }
    else if (i % 2 !== 0){
        document.getElementById("colorpattern").style.backgroundColor = "rgb(189, 189, 189)";
    }

    i = i + 1;

    return (
        <div className="stu">
            <div id="colorpattern">{data.name + " อ." + data.class + " เลขที่ " + data.number}</div>
        </div>
    )
}

export default Data;
