import React, { useState } from 'react'
import firebase from '../utils/firebase'

function AddData(){

    const createData = () => {
        const dataRef = firebase.database().ref('student');
        const data = {
            Name:"นายธันวาพล ผู้อยู่สุข"
        }

        dataRef.push(data)
    }

    return (
        <div>
            <button className='add-btn' onClick={createData}></button>
        </div>
    )
}

export default AddData
